INSERT INTO `lb_criterio` (`id`, `nombre`, `lb_item_id`) VALUES (1, 'El perfil de formación de la institución para la educación media da cuenta de los fines de la educación y de las características propias de la persona que se pretende formar.', 1);
INSERT INTO `lb_criterio` (`id`, `nombre`, `lb_item_id`) VALUES (2, 'Los componentes del currículo responden a un enfoque de formación por competencias', 2);
INSERT INTO `lb_criterio` (`id`, `nombre`, `lb_item_id`) VALUES (3, 'El currículo incorpora e integra los componentes básicos de un enfoque de formación por competencias.', 3);
INSERT INTO `lb_criterio` (`id`, `nombre`, `lb_item_id`) VALUES (4, 'La estructura curricular (componentes del plan de estudios) responde a los lineamientos curriculares del MEN para cada área.', 4);
INSERT INTO `lb_criterio` (`id`, `nombre`, `lb_item_id`) VALUES (5, 'El modelo pedagógico facilita procesos de autogestión y autonomía propios de un enfoque de formación por competencias.', 5);
INSERT INTO `lb_criterio` (`id`, `nombre`, `lb_item_id`) VALUES (6, 'La institución ha tenido alguna experiencia de formación en diseño curricular por competencias.', 6);
INSERT INTO `lb_criterio` (`id`, `nombre`, `lb_item_id`) VALUES (7, 'Los componentes del formato de microcurrículo dan cuenta de  los  elementos necesarios para desarrollar un proceso de formación por competencias.', 7);
INSERT INTO `lb_criterio` (`id`, `nombre`, `lb_item_id`) VALUES (8, 'Los componentes del formato de plan de clase dan cuenta de  los  elementos necesarios para desarrollar un proceso de formación por competencias.', 8);
