INSERT INTO `lb_indicador` (`id`, `nombre`, `lb_criterio_id`) VALUES (1, 'En el perfil de formación expresado por la institución se evidencian elementos que dan cuenta de la formación integral (dimensión política, social, ética, emocional y laboral)', 1);
INSERT INTO `lb_indicador` (`id`, `nombre`, `lb_criterio_id`) VALUES (2, 'Los componentes  del currículo se formulan, integran y desarrollan en función de la potenciación de saberes, habilidades/destrezas y actitudes necesarias para que el estudiante pueda interactuar en diferentes contextos sociales y laborales.', 2);
INSERT INTO `lb_indicador` (`id`, `nombre`, `lb_criterio_id`) VALUES (3, 'Los componentes del currículo incorporan en integran filosofía educativa, propósitos de aprendizaje, ámbitos conceptuales, competencias, estrategias pedagógicas y evaluativas y procesos logísticos que permiten su desarrollo.', 3);
INSERT INTO `lb_indicador` (`id`, `nombre`, `lb_criterio_id`) VALUES (4, 'Los componentes del currículo incorporan e integran: competencias, logros, contenidos (ámbitos conceptuales), estrategias pedagógicas y procesos de evaluación.', 4);
INSERT INTO `lb_indicador` (`id`, `nombre`, `lb_criterio_id`) VALUES (5, 'A partir de los elementos presentados en el microcurrículo de un área de la media, se infiere  un modelo pedagógico  institucional (relación docente- estudiante, contenidos, metodologías, evaluación) coherente con el enfoque curricular por competencias.', 5);
INSERT INTO `lb_indicador` (`id`, `nombre`, `lb_criterio_id`) VALUES (6, 'Formación en diseño curricular por competencias', 6);
INSERT INTO `lb_indicador` (`id`, `nombre`, `lb_criterio_id`) VALUES (7, 'Diseño de currículos por competencias', 6);
INSERT INTO `lb_indicador` (`id`, `nombre`, `lb_criterio_id`) VALUES (8, 'Aplicación de diseño de currículos por competencias', 6);
INSERT INTO `lb_indicador` (`id`, `nombre`, `lb_criterio_id`) VALUES (9, 'El microcurrículo se estructura  a partir de competencia(s), ámbitos conceptuales (conocimientos) logros, indicadores de logro, estrategias pedagógicas (didácticas)  y criterios de evaluación.', 7);
INSERT INTO `lb_indicador` (`id`, `nombre`, `lb_criterio_id`) VALUES (10, 'El formato de planeación de clase tiene como componentes: competencia(s), ámbitos conceptuales (conocimientos) logros, indicadores de logros, estrategias pedagógicas (didácticas)  y criterios de evaluación.', 8);
